import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';

export const TransactionsPage = () => {
  const navigate = useNavigate();
  const [lentItems, setLentItems] = useState([]);
  const [borrowedItems, setBorrowedItems] = useState([]);
  const [listedItems, setListedItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('lent'); // 'lent', 'borrowed', or 'listed'
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const currentUserId = localStorage.getItem('userId');
        setUserId(currentUserId);

        // Fetch all borrows
        const borrowResponse = await fetch('http://localhost:5000/api/borrows/history', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const borrowData = await borrowResponse.json();
        const borrows = Array.isArray(borrowData) ? borrowData : borrowData.borrows || [];

        // Separate into lent and borrowed
        const lent = borrows.filter(b => b.lender?._id === currentUserId || b.lender === currentUserId);
        const borrowed = borrows.filter(b => b.borrower?._id === currentUserId || b.borrower === currentUserId);

        setLentItems(lent);
        setBorrowedItems(borrowed);

        // Fetch user's listed items
        const itemsResponse = await fetch('http://localhost:5000/api/items', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const itemsData = await itemsResponse.json();
        const allItems = Array.isArray(itemsData) ? itemsData : itemsData.items || [];
        
        // Filter to only user's items
        const userItems = allItems.filter(item => item.owner?._id === currentUserId || item.owner === currentUserId);
        
        // Populate who borrowed each item
        const enrichedItems = userItems.map(item => {
          const activeBorrow = borrows.find(b => b.item?._id === item._id && b.status === 'active');
          const lastBorrow = borrows.find(b => b.item?._id === item._id && b.status === 'returned');
          return {
            ...item,
            currentBorrow: activeBorrow,
            lastBorrow: lastBorrow,
          };
        });
        
        setListedItems(enrichedItems);
      } catch (error) {
        console.error('Failed to fetch transactions:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchTransactions();
  }, []);

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      case 'active':
        return 'bg-blue-100 text-blue-800';
      case 'returned':
        return 'bg-green-100 text-green-800';
      case 'overdue':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (date) => {
    return new Date(date).toLocaleDateString();
  };

  const daysUntilDue = (date) => {
    const today = new Date();
    const due = new Date(date);
    const diff = Math.ceil((due - today) / (1000 * 60 * 60 * 24));
    return diff;
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-4xl font-bold text-gray-800">My Transactions</h1>
        <button
          onClick={() => navigate('/dashboard')}
          className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg"
        >
          Back to Dashboard
        </button>
      </div>

      {/* Tabs */}
      <div className="flex gap-4 mb-8 border-b-2 border-gray-200">
        <button
          onClick={() => setActiveTab('lent')}
          className={`pb-4 px-6 font-semibold text-lg transition ${
            activeTab === 'lent'
              ? 'border-b-4 border-green-600 text-green-600'
              : 'text-gray-600 hover:text-gray-800'
          }`}
        >
          ðŸ“¤ Items I Lent ({lentItems.length})
        </button>
        <button
          onClick={() => setActiveTab('borrowed')}
          className={`pb-4 px-6 font-semibold text-lg transition ${
            activeTab === 'borrowed'
              ? 'border-b-4 border-blue-600 text-blue-600'
              : 'text-gray-600 hover:text-gray-800'
          }`}
        >
          ðŸ“¥ Items I Borrowed ({borrowedItems.length})
        </button>
      </div>

      {loading ? (
        <p className="text-center py-12">Loading transactions...</p>
      ) : (
        <div>
          {/* Items I Lent */}
          {activeTab === 'lent' && (
            <div>
              {lentItems.length === 0 ? (
                <div className="text-center py-12 bg-white rounded-lg">
                  <p className="text-gray-600 text-lg">You haven't lent any items yet</p>
                  <p className="text-gray-500 mt-2">List items and wait for borrowers to request them!</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {lentItems.map((transaction) => (
                    <div
                      key={transaction._id}
                      className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition border-l-4 border-green-600"
                    >
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        {/* Item Info */}
                        <div>
                          <h3 className="text-xl font-bold text-gray-800 mb-2">
                            ðŸ“¦ {transaction.item?.name}
                          </h3>
                          <p className="text-gray-600 text-sm mb-4">
                            {transaction.item?.description}
                          </p>
                          <div className="space-y-2 text-sm">
                            <p>
                              <span className="font-semibold text-gray-700">Lent to:</span>{' '}
                              <span className="text-blue-600 font-bold">{transaction.borrower?.name}</span>
                            </p>
                            <p>
                              <span className="font-semibold text-gray-700">Phone:</span>{' '}
                              <span className="text-gray-600">{transaction.borrower?.phone}</span>
                            </p>
                            <p>
                              <span className="font-semibold text-gray-700">Trust Score:</span>{' '}
                              <span className={transaction.borrower?.trustScore >= 70 ? 'text-green-600 font-bold' : 'text-orange-600 font-bold'}>
                                {transaction.borrower?.trustScore || 50}
                              </span>
                            </p>
                          </div>
                        </div>

                        {/* Dates & Status */}
                        <div>
                          <div className="space-y-3">
                            <div className="bg-gray-50 p-4 rounded-lg">
                              <p className="text-gray-600 text-sm">Lent on</p>
                              <p className="text-lg font-bold text-gray-800">
                                {formatDate(transaction.borrowDate)}
                              </p>
                            </div>

                            <div className="bg-gray-50 p-4 rounded-lg">
                              <p className="text-gray-600 text-sm">Expected return</p>
                              <p className="text-lg font-bold text-gray-800">
                                {formatDate(transaction.expectedReturnDate)}
                              </p>
                              {transaction.status === 'active' && (
                                <p className={`text-sm mt-2 ${daysUntilDue(transaction.expectedReturnDate) <= 0 ? 'text-red-600' : 'text-green-600'} font-semibold`}>
                                  {daysUntilDue(transaction.expectedReturnDate) > 0
                                    ? `${daysUntilDue(transaction.expectedReturnDate)} days left`
                                    : 'OVERDUE!'}
                                </p>
                              )}
                            </div>

                            <div>
                              <span className={`inline-block px-4 py-2 rounded-full text-sm font-semibold ${getStatusColor(transaction.status)}`}>
                                {transaction.status.toUpperCase()}
                              </span>
                            </div>

                            {transaction.status === 'returned' && transaction.actualReturnDate && (
                              <div className="bg-green-50 p-4 rounded-lg">
                                <p className="text-green-700 text-sm">âœ“ Returned on</p>
                                <p className="text-lg font-bold text-green-800">
                                  {formatDate(transaction.actualReturnDate)}
                                </p>
                              </div>
                            )}
                          </div>
                        </div>
                      </div>

                      {/* Actions */}
                      {transaction.status === 'active' && (
                        <div className="mt-4 pt-4 border-t">
                          <button className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg text-sm font-semibold">
                            Mark as Returned
                          </button>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {/* Items I Borrowed */}
          {activeTab === 'borrowed' && (
            <div>
              {borrowedItems.length === 0 ? (
                <div className="text-center py-12 bg-white rounded-lg">
                  <p className="text-gray-600 text-lg">You haven't borrowed any items yet</p>
                  <button
                    onClick={() => navigate('/items')}
                    className="mt-4 bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg"
                  >
                    Search Items to Borrow
                  </button>
                </div>
              ) : (
                <div className="space-y-4">
                  {borrowedItems.map((transaction) => (
                    <div
                      key={transaction._id}
                      className="bg-white rounded-lg shadow-md p-6 hover:shadow-lg transition border-l-4 border-blue-600"
                    >
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        {/* Item Info */}
                        <div>
                          <h3 className="text-xl font-bold text-gray-800 mb-2">
                            ðŸ“¦ {transaction.item?.name}
                          </h3>
                          <p className="text-gray-600 text-sm mb-4">
                            {transaction.item?.description}
                          </p>
                          <div className="space-y-2 text-sm">
                            <p>
                              <span className="font-semibold text-gray-700">Borrowed from:</span>{' '}
                              <span className="text-green-600 font-bold">{transaction.lender?.name}</span>
                            </p>
                            <p>
                              <span className="font-semibold text-gray-700">Phone:</span>{' '}
                              <span className="text-gray-600">{transaction.lender?.phone}</span>
                            </p>
                            <p>
                              <span className="font-semibold text-gray-700">Trust Score:</span>{' '}
                              <span className={transaction.lender?.trustScore >= 70 ? 'text-green-600 font-bold' : 'text-orange-600 font-bold'}>
                                {transaction.lender?.trustScore || 50}
                              </span>
                            </p>
                          </div>
                        </div>

                        {/* Dates & Status */}
                        <div>
                          <div className="space-y-3">
                            <div className="bg-gray-50 p-4 rounded-lg">
                              <p className="text-gray-600 text-sm">Borrowed on</p>
                              <p className="text-lg font-bold text-gray-800">
                                {formatDate(transaction.borrowDate)}
                              </p>
                            </div>

                            <div className="bg-gray-50 p-4 rounded-lg">
                              <p className="text-gray-600 text-sm">Must return by</p>
                              <p className="text-lg font-bold text-gray-800">
                                {formatDate(transaction.expectedReturnDate)}
                              </p>
                              {transaction.status === 'active' && (
                                <p className={`text-sm mt-2 ${daysUntilDue(transaction.expectedReturnDate) <= 0 ? 'text-red-600' : 'text-green-600'} font-semibold`}>
                                  {daysUntilDue(transaction.expectedReturnDate) > 0
                                    ? `${daysUntilDue(transaction.expectedReturnDate)} days left`
                                    : 'OVERDUE!'}
                                </p>
                              )}
                            </div>

                            <div>
                              <span className={`inline-block px-4 py-2 rounded-full text-sm font-semibold ${getStatusColor(transaction.status)}`}>
                                {transaction.status.toUpperCase()}
                              </span>
                            </div>

                            {transaction.status === 'returned' && transaction.actualReturnDate && (
                              <div className="bg-green-50 p-4 rounded-lg">
                                <p className="text-green-700 text-sm">âœ“ Returned on</p>
                                <p className="text-lg font-bold text-green-800">
                                  {formatDate(transaction.actualReturnDate)}
                                </p>
                              </div>
                            )}
                          </div>
                        </div>
                      </div>

                      {/* Actions */}
                      {transaction.status === 'active' && (
                        <div className="mt-4 pt-4 border-t">
                          <button className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-semibold">
                            Mark as Returned
                          </button>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
};
