import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';

export const ItemsPage = () => {
  const navigate = useNavigate();
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [borrowRequest, setBorrowRequest] = useState(null);

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/items', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        setItems(Array.isArray(data) ? data : data.items || []);
      } catch (error) {
        console.error('Failed to fetch items:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchItems();
  }, []);

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-4xl font-bold text-gray-800">Available Items</h1>
        <button
          onClick={() => navigate('/dashboard')}
          className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg"
        >
          Back to Dashboard
        </button>
      </div>

      {loading ? (
        <p className="text-center py-8">Loading items...</p>
      ) : items.length === 0 ? (
        <div className="text-center py-12 bg-white rounded-lg">
          <p className="text-gray-600 text-lg">No items available yet</p>
          <button
            onClick={() => navigate('/dashboard')}
            className="mt-4 bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded-lg"
          >
            List Your First Item
          </button>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {items.map((item) => (
            <div key={item._id} className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition">
              <div className="bg-gray-200 h-48 flex items-center justify-center">
                <span className="text-gray-400 text-4xl">ðŸ“¦</span>
              </div>
              <div className="p-4">
                <h3 className="text-xl font-bold text-gray-800">{item.name}</h3>
                <p className="text-gray-600 text-sm mt-2">{item.description}</p>
                <p className="text-xs text-gray-500 mt-2">Owner: {item.owner?.name || 'Unknown'}</p>
                <div className="flex justify-between items-center mt-4">
                  <span className="bg-green-100 text-green-800 px-3 py-1 rounded-full text-xs font-semibold">
                    {item.category}
                  </span>
                  <button 
                    onClick={async () => {
                      try {
                        const response = await fetch('http://localhost:5000/api/borrows', {
                          method: 'POST',
                          headers: {
                            'Content-Type': 'application/json',
                            'Authorization': `Bearer ${localStorage.getItem('token')}`,
                          },
                          body: JSON.stringify({
                            item: item._id,
                            expectedReturnDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000), // 7 days
                          }),
                        });
                        const data = await response.json();
                        if (response.ok) {
                          alert('Borrow request sent successfully!');
                          setBorrowRequest(item._id);
                        } else {
                          alert('Error: ' + (data.message || 'Failed to request borrow'));
                        }
                      } catch (error) {
                        alert('Error: ' + error.message);
                      }
                    }}
                    disabled={borrowRequest === item._id}
                    className={`${
                      borrowRequest === item._id 
                        ? 'bg-gray-400' 
                        : 'bg-blue-600 hover:bg-blue-700'
                    } text-white px-4 py-2 rounded-lg text-sm disabled:cursor-not-allowed`}
                  >
                    {borrowRequest === item._id ? 'âœ“ Requested' : 'Request Borrow'}
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};
